/* eslint-disable */

<template>
	<div class="container">
		<h2>Socket Controller</h2>
		<p>{{ msg }}</p>
		<button v-on:click="groups"> WTF </button>
		<div class="card">
			<div class="outlets">
				<div class="group group-double" v-for="group in _groups">
					hai
						<button v-for="socket in group.sockets" class="socket socket-double always-on" v-on:click="test">
							{{ socket.id }} - a
						</button>
				</div>

					<div class="group group-double">
						<button class="socket socket-double always-on" v-on:click="test">
							1
						</button>

						<button class="socket socket-double always-on">
							2
						</button>
					</div>

					<div class="group group-double">
						<button class="socket socket-double">
							3
						</button>

						<button class="socket socket-double">
							4
						</button>
					</div>

					<div class="group group-double">
						<button class="socket socket-single">
							5
						</button>
						<button class="socket socket-single">
							6
						</button>
					</div>

					<div class="group group-double">
						<button class="socket socket-single">
							7
						</button>
						<button class="socket socket-single">
							8
						</button>
					</div>
			</div>
		</div>
	</div>
</template>

<script>
	const axios = require('axios');

	export default {
		name: 'app',
		data: () => {
			return {
				hello: 'Hai gais!',
				msg: `System: OK...`,
				_groups: [],
				_sockets: [],
				current_layout: {}
			}
		},
		mounted: function () {
			console.log('app is mounted');
			this.groups();
		},
		methods: {
			groups: (what) => {
				console.log('groups called...', what);
				axios.get('http://localhost:8888/groups').then(
					(res, err) => {
						if(err) {
							console.log('error: ', err);
							return;
						};
						let g = res.data && res.data.groups ? res.data.groups : [];
						console.log('groups fetched: ', res);
						return g;
					});
			},
			test: (check) => {
				console.log('checking: ', check)
			}
		}
	}
</script>

<style scoped lang="css">
	@import 'styles.css';
</style>
